document.getElementById("loginSubmit").onclick=function(){sendAsyncRequest("POST","/api/login","username="+document.getElementById("username").value+"&password="+document.getElementById("password").value,(function(h){if(4==h.readyState)if(200==h.status)window.location.href="/home";else if(403==h.status){let errField=document.getElementById("err");errField.innerText="Incorrect username/password",errField.setAttribute("style","display:block; color:red;")}}))};